class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long ans = 0;
        priority_queue<pair<int,int>>pq;
        for(auto it : v){
            pq.push({it, 0});
        }
        for(auto it : h){
            pq.push({it, 1});
        }

        int cntH = 1, cntV = 1;
        while(!pq.empty()) {
            int t = pq.top().first, d = pq.top().second;
            pq.pop();

            if(d == 0){
                ans += (cntH * t);
                cntV++;
            }
            else{
                ans += (cntV * t);
                cntH++;
            }
        }
        return ans;
    }
};
