class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& v1, vector<int>& v2) {
        vector<pair<int,int>> v;
        for(int i = 0;i<v1.size();i++){
            v.push_back({v1[i],0});
        }
        for(int i = 0;i<v2.size();i++){
            v.push_back({v2[i],1});
        }
        sort(v.begin(),v.end()); int mulc = 1; int mulr = 1; reverse(v.begin(),v.end()); long long tot = 0;
        for(int i = 0;i<v.size();i++){
            if(v[i].second==1){
                tot += (v[i].first*(1LL)*mulc); mulr++;
            }
            else{
                tot += (v[i].first*(1LL)*mulr); mulc++;
            }
        }
        return tot;
    }
};
