class Solution:
    def minimumCost(self, m: int, n: int, horiCut: List[int], vertCut: List[int]) -> int:
        hp, vp, ans = 1,1,0
        cutlst = [[hori, 'h'] for hori in horiCut] + [[vert,'v'] for vert in vertCut]
        cutlst.sort(reverse=True,key=lambda x:x[0])

        for i,j in cutlst:
            if j=='h':
                ans += i*vp
                hp += 1
            else:
                ans += i * hp
                vp += 1
        return ans
