class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCut, vector<int>& vCut) {
       sort(hCut.rbegin(), hCut.rend());
    sort(vCut.rbegin(), vCut.rend());

    long long  hSeg = 1;
    long long  vSeg = 1;
    long long  cost = 0;

    long long  hIdx = 0, vIdx = 0;

    while (hIdx < hCut.size() && vIdx < vCut.size()) {
        if (hCut[hIdx] >= vCut[vIdx]) {
            cost += hCut[hIdx] * vSeg;
            hSeg++;
            hIdx++;
        } else {
            cost += vCut[vIdx] * hSeg;
            vSeg++;
            vIdx++;
        }
    }

    while (hIdx < hCut.size()) {
        cost += hCut[hIdx] * vSeg;
        hSeg++;
        hIdx++;
    }

    while (vIdx < vCut.size()) {
        cost += vCut[vIdx] * hSeg;
        vSeg++;
        vIdx++;
    }

    return cost;
    }
};
