class Solution {
public:
    long long answer(long long rows, long long cols, int m, int n, vector<pair<long long,long long >>&track)
    {
       long long ans = 0;
       for(auto i : track)
       {
           if(i.second==1)
           {
                ans = ans + cols*i.first;
                rows++;
           }
           else
           {
                ans = ans + rows*i.first;
                cols++;
           }
       }
       return ans;
    }
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<long long, long long>>track;
        for(int i = 0; i < m -1; i++) track.push_back(make_pair(horizontalCut[i],1));
        for(int i = 0; i < n-1; i++) track.push_back(make_pair(verticalCut[i],2));
        sort(track.begin(),track.end());
        reverse(track.begin(),track.end());
        return answer(1,1,m,n,track);
    }
};
