class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& a, vector<int>& b) {
        sort(a.rbegin(), a.rend());
        sort(b.rbegin(), b.rend());
        long long s = accumulate(a.begin(), a.end(), 0ll);
        long long t = accumulate(b.begin(), b.end(), 0ll);
        long long ans = 0;
        int i = 0, j = 0;
        while(i < m -1 && j < n-1){
            if(a[i]>b[j] || (a[i] == b[j] && s>=t)){
                ans += a[i] + t;
                s -= a[i];
                ++i;
            } else {
                ans += b[j] + s;
                t -= b[j];
                ++j;
            }
        }
        return ans + s + t;
    }
};
