typedef long long ll;typedef pair<int, int> pi;
class Solution {
public:
    ll minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        ll ret = 0;
        vector<pi> v;
        for(int i = 0; i < m - 1; i++) {
            v.push_back({horizontalCut[i], 0});
        } 
        for(int i = 0; i < n - 1; i++) {
            v.push_back({verticalCut[i], 1});
        } 
        sort(v.begin(), v.end());
        reverse(v.begin(), v.end());
        int a = 1, b = 1;
        for(auto [i, j]: v) {
            if(j == 0) {
                ret += i * b;
            }
            else {
                ret += i * a;
            }

            if(j == 0) a++;
            else b++;
        }
        return ret;
    }
};
