class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        nchoose = []
        for h in horizontalCut:
            nchoose.append((h, "h"))
        for v in verticalCut:
            nchoose.append((v, "v"))
        nchoose.sort(reverse=True)
        # print(nchoose)
        ans = 0
        v = 1
        h = 1
        for c, ch in nchoose:
            # print(c, ch, v, h)
            if ch == 'v':
                ans += c * h 
                v += 1
            else:
                ans += c * v 
                h += 1
        return ans
