class Solution:
    def minimumCost(self, m: int, n: int, horiz: List[int], vert: List[int]) -> int:
        horiz.sort()
        vert.sort()
        hpcs = 1
        vpcs = 1
        total = 0
        while horiz and vert:
            if horiz[-1] > vert[-1]:
                hpcs += 1
                total += horiz.pop() * vpcs
            else:
                vpcs += 1
                total += vert.pop() * hpcs
        while horiz:
            hpcs += 1
            total += horiz.pop() * vpcs
        while vert:
            vpcs += 1
            total += vert.pop() * hpcs
        return total
