class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.begin(), hc.end(), [](int a, int b){
            return a>b;
        });
        sort(vc.begin(), vc.end(), [](int a, int b){
            return a>b;
        });
        int xc = 1, yc = 1;
        int i = 0, j = 0;
        long long ans = 0;
        while(i<hc.size() || j<vc.size()){
            if(i==hc.size()){
                ans += vc[j]*xc;
                j++;
            }
            else if(j==vc.size()){
                ans += hc[i]*yc;
                i++;
            }
            else if(hc[i]>=vc[j]){
                ans += hc[i]*yc;
                i++;
                xc++;
            }
            else{
                ans += vc[j]*xc;
                j++;
                yc++;
            }
        }
        return ans;
    }
};
