class Solution {
    public long minimumCost(int m, int n, int[] h, int[] v) {
        long ver = 1, hor = 1;
        List<Integer> hlist = new ArrayList<>();
        List<Integer> vlist = new ArrayList<>();
        long ans = 0;
        for(int i=0;i<h.length;i++){
            hlist.add(i);
        }
        for(int i=0;i<v.length;i++){
            vlist.add(i);
        }

        Collections.sort(hlist, (a,b) -> (h[b]-h[a]));
        Collections.sort(vlist, (a,b) -> (v[b]-v[a]));

        int i = 0, j = 0;
        while(i<m-1 && j<n-1){
            int hind = hlist.get(i);
            int vind = vlist.get(j);

            if(h[hind] > v[vind]){
                ans += ver*h[hind];
                hor++;
                i++;
            }
            else if(h[hind] < v[vind]){
                ans += hor*v[vind];
                ver++;
                j++;
            }
            else{
                if(ver < hor){
                    ans += hor*v[vind];
                    ver++;
                    j++;
                }
                else{
                    ans += ver*h[hind];
                    hor++;
                    i++;
                }
            }
        }

        while(i<m-1){
            int hind = hlist.get(i);
            ans += ver*h[hind];
            hor++;
            i++;
        }
        while(j<n-1){
            int vind = vlist.get(j);
            ans += hor*v[vind];
            ver++;
            j++;
        }
        return ans;
    }
}
