class Solution {
    public long minimumCost(int m, int n, int[] hori, int[] verti) {
        Arrays.sort(verti);
        Arrays.sort(hori);
        int h=hori.length-1;
        int v=verti.length-1;
        int hp=1;
        int vp=1;
        long cost=0;

        while(h>=0 && v>=0)
        {
            if(verti[v]<=hori[h])
            {
                cost+=(hori[h]*vp);
                hp++;
                h--;
            }
            else
            {
                cost+=verti[v]*hp;
                vp++;
                v--;
            }
        } 
        while(h>=0)
        {
            cost+=(hori[h]*vp);
            hp++;
            h--;
        }
        while(v>=0)
        {
            cost+=verti[v]*hp;
            vp++;
            v--;
        }   
        return cost;
    }
}
