class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        all_cuts = []
        for cut in horizontalCut:
            all_cuts.append((cut, 'h'))
        for cut in verticalCut:
            all_cuts.append((cut, 'v'))

        all_cuts.sort(key=lambda x: x[0], reverse=True)
        performed_horizontal_cuts, performed_vertical_cuts = 0, 0
        cost = 0

        for cut_cost, axis in all_cuts:
            if axis == 'h':
                cost += cut_cost * (performed_vertical_cuts + 1)
                performed_horizontal_cuts += 1
            else:
                cost += cut_cost * (performed_horizontal_cuts + 1)
                performed_vertical_cuts += 1

        return cost
