#define ll long long
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& v1, vector<int>& v2) {
         ll i,j,count=0,ans=1e9;
        set<ll> st1,st2;
        i=0,j=0;
        if(m==1 && n==1) return 0;
        sort(v1.begin(), v1.end(), greater<int>());
         sort(v2.begin(), v2.end(), greater<int>());
        while(i<(m-1) && (j< n-1)){

            if(v1[i] > v2[j]){
                count += (st2.size() +1)*v1[i];
                st1.insert(i);
                i++;
            }
            else{
                count += (st1.size() +1)*v2[j];
                st2.insert(j);
                j++; 
            }
        }
        while(i< (m-1)){
            count += (v1[i] * (n));
            i++;
        }
        while(j< (n-1)){
            count += (v2[j]*m);
            j++;
        }
        return count;
    }
};
