class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        all_cuts = [(cost, 'H') for cost in horizontalCut] + [(cost, 'V') for cost in verticalCut]
        all_cuts.sort()

        horizontal_segments = 1
        vertical_segments = 1
        total_cost = 0

        for cost, cut_type in reversed(all_cuts):
            if cut_type == 'H':
                total_cost += cost * vertical_segments
                horizontal_segments += 1
            else:
                total_cost += cost * horizontal_segments
                vertical_segments += 1

        return total_cost
