class Solution {
public:

    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long Ans = 0;
        vector<pair<long long, char>> V;
        for(int i=0; i<horizontalCut.size(); i++){
            V.push_back({horizontalCut[i],'H'});
        }
        for(int i=0; i<verticalCut.size(); i++){
            V.push_back({verticalCut[i],'V'});
        }
        sort(V.rbegin(), V.rend());
        long long N = 1, M=1;

        for(auto x:V){
            if(x.second=='H'){
                  Ans += x.first*M;
                  N++;
            }
            else{
                 Ans += x.first*N;
                 M++;
            }
        }

        return Ans;
    }
};
