class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        horizontalCut = sorted(horizontalCut, reverse=True)
        verticalCut = sorted(verticalCut, reverse=True)
        cntV, cntH = 1, 1
        idxV, idxH = 0, 0
        ans = 0
        while idxV < len(verticalCut) and idxH < len(horizontalCut):
            if verticalCut[idxV] > horizontalCut[idxH]:
                ans += verticalCut[idxV] * cntH
                cntV += 1
                idxV += 1
            else:
                ans += horizontalCut[idxH] * cntV
                cntH += 1
                idxH += 1
        while idxH < len(horizontalCut):
            ans += horizontalCut[idxH] * cntV
            idxH += 1
        while idxV < len(verticalCut):
            ans += verticalCut[idxV] * cntH
            idxV += 1
        return ans
