#define ll long long
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
    sort(h.begin(), h.end(),greater<int>());
    sort(v.begin(), v.end(),greater<int>());

    ll int x = 1; 
    ll int y = 1; 
    long long int cnt = 0;

    ll int i = 0, j = 0;
    while (i < h.size() && j < v.size()) {
        if (h[i] >= v[j]) {

            cnt += h[i] * y;
            x++;
            i++;
        } else {

            cnt += v[j] * x;
            y++;
            j++;
        }
    }


    while (i < h.size()) {
        cnt += h[i] * y;
        x++;
        i++;
    }


    while (j < v.size()) {
        cnt += v[j] * x;
        y++;
        j++;
    }

    return cnt;
    }
};
