class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.begin(), hc.end(),greater<int>());
        sort(vc.begin(), vc.end(),greater<int>());

        long long i = 0, j = 0;
        long long hcount = 1, vcount = 1;
        long long ans = 0;

        while (i < hc.size() && j < vc.size()) {
            if (hc[i] > vc[j]) {
                ans += hc[i] * vcount;
                hcount++;
                i++;
            } else {
                ans += vc[j] * hcount;
                vcount++;
                j++;
            }
        }
        while (i < hc.size()) {
            ans += hc[i] * vcount;
            i++;
        }
        while (j < vc.size()) {
            ans += vc[j] * hcount;
            j++;
        }
        return ans; 
    }
};
