class Solution {
    fun minimumCost(m: Int, n: Int, horizontalCut: IntArray, verticalCut: IntArray): Long {
        var h = horizontalCut.sorted().reversed()
        var v = verticalCut.sorted().reversed()
        var res = 0L;var hP = 1;var vP = 1;var i = 0;var j = 0
        while (i < h.size && j < v.size) {
            if (h[i] >= v[j]) {
                res += h[i] * vP
                hP++
                i++
            } else {
                res += v[j] * hP
                vP++
                j++
            }
        }
        while (i < h.size) {
            res += h[i] * vP
            i++
        }
        while (j < v.size) {
            res += v[j] * hP
            j++
        }
        return res
    }
}
