#include <vector>
#include <queue>
#include <functional>

using namespace std;

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCuts, vector<int>& vCuts) {
        priority_queue<pair<int, char>> pq;

        for (int cost : hCuts) {
            pq.emplace(cost, 'H');
        }
        for (int cost : vCuts) {
            pq.emplace(cost, 'V');
        }

        long long totalCost = 0;
        int hCount = 1;  
        int vCount = 1;  

        while (!pq.empty()) {
            auto [cost, type] = pq.top();
            pq.pop();

            if (type == 'H') {
                totalCost += static_cast<long long>(cost) * vCount;
                hCount++;
            } else {
                totalCost += static_cast<long long>(cost) * hCount;
                vCount++;
            }
        }

        return totalCost;
    }
};
