class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
   vector<pair<long long,bool>> v;
    for (int i : horizontalCut) {
        v.push_back({i, true});
    }
    for (int i : verticalCut) {
        v.push_back({i, false});
    }
    sort(v.begin(), v.end(), [&]( pair<int,bool>a, pair<int,bool>b) {
        return a.first > b.first;
    });

    long long total = 0;
    long long ct1 = 1;
    long long ct2 = 1;

    for (auto p : v) {
        if (p.second) {
            total += p.first * ct2;
            ct1++;
        } else {

            total+= p.first * ct1;
            ct2++;
        }
    }

    return total;
    }
};
