class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        buf = []
        for i in horizontalCut:
            buf.append((i,0))
        for j in verticalCut:
            buf.append((j,1))
        x = 1
        y = 1
        ans = 0
        buf.sort(key = lambda n:n[0], reverse = True)
        for i,j in buf:
            if j == 0:
                ans += i * x
                y += 1
            else:
                ans += i * y
                x += 1
        return ans
