class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long res=0;
        for(long long q:h){
            res+=q*n;
        }
        for(long long q:v){
            res+=q*m;
        }
        sort(h.begin(),h.end());
        sort(v.begin(),v.end());
        int l=0;
        for(int i=0;i+1<m;++i){
            while(l+1<n&&v[l]<=h[i]){
                ++l;
            }
            res-=(long long)l*h[i];
        }
        l=0;
        for(int i=0;i+1<n;++i){
            while(l+1<m&&h[l]<v[i]){
                ++l;
            }
            res-=(long long)l*v[i];
        }
        return res;
    }
};
