class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        long long cost = 0;
        vector<pair<int,int>> v;
        for(auto x : hc){
            v.push_back({x,0});
        }
        for(auto x : vc) v.push_back({x,1});
        sort(v.begin(),v.end());
        int h = 1 , vcc = 1;
        for(int i = v.size()-1 ; i>=0 ; i--){
            if(v[i].second==0){
                cost += (v[i].first * vcc);
                h++;
            }
            else{
                cost += (v[i].first*h);
                vcc++;
            }
        }
        return cost;
    }
};
