using ll = long long;

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(), h.end(), greater<>());
        sort(v.begin(), v.end(), greater<>());

        ll ret = 0;

        int p1 = 1, p2 = 1;

        int i = 0, j = 0;

        while (i < h.size() and j < v.size()) {
            if (h[i] > v[j]) {
                ret += 1LL * h[i] * p2; ++p1;
                ++i;
            } else {
                ret += 1LL * v[j] * p1; ++p2;
                ++j;
            }
        }

        int rem = 0;
        while (i < h.size()) {
            rem += h[i];
            ++i;
        }

        ret += 1LL * rem * p2;

        rem = 0;
        while (j < v.size()) {
            rem += v[j];
            ++j;
        }

        ret += 1LL * rem * p1;

        return ret;
    }
};
