class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
        sort(hor.rbegin(), hor.rend());
        sort(ver.rbegin(), ver.rend());
        long long tot = 0, hCnt = 1, vCnt = 1;
        size_t hInd = 0, vInd = 0;

        while (hInd < hor.size() && vInd < ver.size()) {
            if (hor[hInd] >= ver[vInd]) {
                tot += hor[hInd++] * vCnt;
                hCnt++;
            } else {
                tot += ver[vInd++] * hCnt;
                vCnt++;
            }
        }

        while (hInd < hor.size()) tot += hor[hInd++] * vCnt;
        while (vInd < ver.size()) tot += ver[vInd++] * hCnt;

        return tot;
    }
};
