class Solution:
    def minimumCost(self, m: int, n: int, h: List[int], v: List[int]) -> int:
        h.sort(reverse=True)
        v.sort(reverse=True)
        ans = 0
        ph = 0
        pv = 0
        while ph < m - 1 or pv < n - 1:
            if ph == m - 1:
                ans += v[pv] * (ph + 1)
                pv += 1
            elif pv == n - 1:
                ans += h[ph] * (pv + 1)
                ph += 1
            else:
                if h[ph] > v[pv]:
                    ans += h[ph] * (pv + 1)
                    ph += 1
                else:
                    ans += v[pv] * (ph + 1)
                    pv += 1

        return ans
