/**
 * @param {number} m
 * @param {number} n
 * @param {number[]} horizontalCut
 * @param {number[]} verticalCut
 * @return {number}
 */
var minimumCost = function(m, n, horizontalCut, verticalCut) {
     const ops = []
    for (let i = 0; i < horizontalCut.length; i++) {
        ops.push([horizontalCut[i], 0])
    }
    for (let i = 0; i < verticalCut.length; i++) {
        ops.push([verticalCut[i], 1])
    }
    ops.sort((a, b) => b[0] - a[0])

    // console.log('ops', ops)

    let ans = 0, i = 0, col = 0, row = 0
    while (i < ops.length) {
        const [cost, dirction] = ops[i]
        if (dirction === 0) {
            row++
            ans += cost * (col + 1)
        } else {
            col++
            ans += cost * (row + 1)
        }
        i++
    }
    return ans
};
