class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.begin(),horizontalCut.end());
        sort(verticalCut.begin(),verticalCut.end());
        int i,j;
        long long s=0,t=0,ans=0;
        for(i=j=0;i+1<m&&j+1<n;)if(horizontalCut[i]<verticalCut[j])
        {
            ans+=horizontalCut[i]+t;
            s+=horizontalCut[i];
            i++;
        }
        else
        {
            ans+=verticalCut[j]+s;
            t+=verticalCut[j];
            j++;
        }
        for(;i+1<m;i++)
        {
            ans+=horizontalCut[i]+t;
            s+=horizontalCut[i];
        }
        for(;j+1<n;j++)
        {
            ans+=verticalCut[j]+s;
            t+=verticalCut[j];
        }
        return ans;
    }
};
