class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        long long ans=0;
        sort(hc.begin(),hc.end());
        reverse(hc.begin(),hc.end());
        sort(vc.begin(),vc.end());
        reverse(vc.begin(),vc.end());

        int h=0;
        int v=0;

        int i=0;
        int j=0;
        while(i<m-1 && j<n-1){
            if(hc[i]>vc[j]){
                ans+=(v+1)*hc[i];
                h++;
                i++;
            }
            else{
                ans+=(h+1)*vc[j];
                v++;
                j++;
            }
        }
        while(i<m-1){
            ans+=(v+1)*(hc[i]);
            i++;
        }
        while(j<n-1){
            ans+=(h+1)*vc[j];
            j++;
        }
        return ans;
    }
};
