record Data(int cost, int horizon) {} // h is 0, v is 1
class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
      var buf = new LinkedList<Data>();
      for (var h : horizontalCut) {
        buf.addLast(new Data(h, 0));
      }
      for (var v : verticalCut) {
        buf.addLast(new Data(v, 1));
      }
      Collections.sort(buf, (a,b)->b.cost()-a.cost());
      var ret = 0L;
      var h = 1;
      var v = 1;

      for (var b : buf) {
        var dir = b.horizon();
        if (dir == 0) {
          ret += b.cost() * v;
          h++;
        } else {
          ret += b.cost() * h;
          v++;
        }
      }
      return ret;
    }
}
