import heapq
class Solution(object):
    def minimumCost(self, m, n, h, v):
        har=[]
        for i in h:
            har.append(-i)
        var=[]
        for i in v:
            var.append(-i)
        heapq.heapify(har)
        heapq.heapify(var)
        hpc=1
        vpc=1
        ans=0
        while har and var:
            if har[0]<var[0]:
                ans-=heapq.heappop(har)*vpc
                hpc+=1
            else:
                ans-=heapq.heappop(var)*hpc
                vpc+=1
        while har:
            ans-=heapq.heappop(har)*vpc
        while var:
            ans-=heapq.heappop(var)*hpc
        return ans
