type Cut struct {
	cost  int
	isHor bool
}

func minimumCost(m int, n int, horizontalCut []int, verticalCut []int) int64 {
    cuts := []Cut{}

	for _, cost := range horizontalCut {
		cuts = append(cuts, Cut{cost, true})
	}

	for _, cost := range verticalCut {
		cuts = append(cuts, Cut{cost, false})
	}

	sort.Slice(cuts, func(i, j int) bool {
		return cuts[i].cost > cuts[j].cost
	})

	var totalCost int64 = 0
	var horPieces int64 = 1
	var verPieces int64 = 1

	for _, cut := range cuts {
		if cut.isHor {
			totalCost += int64(cut.cost) * verPieces
			horPieces++
		} else {
			totalCost += int64(cut.cost) * horPieces
			verPieces++
		}
	}

	return totalCost
}
