class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& hcut, vector<int>& vcut) {
        long long i = n - 2;
            long long j = m - 2;
        sort(hcut.begin(),hcut.end());
        sort(vcut.begin(),vcut.end());
            long long hsum = 0, vsum = 0;

            for(auto i : hcut){hsum += i;}
        for(auto i : vcut){vsum += i;}


        long long ans = 0;


        while(i>=0||j>=0)
        {
            if(j<0 || (i>=0 && hcut[i] >= vcut[j]))
            {
                hsum -= hcut[i];
                ans += vsum;
                ans += hcut[i];
                i--;
            }
            else if(i<0 || (j>=0 && vcut[j] >= hcut[i]))
            {
                vsum -= vcut[j];
                ans += hsum;
                ans += vcut[j];
                j--;
            }
        }
        return ans;
    }
};
