class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.begin(),hc.end(),greater<>());
        sort(vc.begin(),vc.end(),greater<>());

        long long h=1;
        long long v=1;

        int i=0;
        int j=0;

        long long total=0;
        while(i<hc.size() && j<vc.size()){
            if(hc[i]>vc[j]){
                total+= (long long)hc[i] * v;
                i++;
                h++;
            }
            else {
                total+=(long long)vc[j] * h;
                j++;
                v++;
            }
        }
        while(i<hc.size())
        {
            total+=hc[i]*v;
            i++;
            h++;
        }
        while(j<vc.size()){
            total+=vc[j]*h;
            j++;
            v++;
        }
        return total;
    }
};
