class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(),h.end());
        sort(v.begin(),v.end());
        int i=h.size()-1;
        int j=v.size()-1;
        int vc=1;
        int hc=1;
        long long ans=0;
        while(i>=0 || j>=0){
            if((i>=0 && j<0)|| (i>=0 && h[i]>v[j])){
                ans+=h[i]*vc;
                hc++;
                i--;
            }else{
                ans+=v[j]*hc;
                vc++;
                j--;
            }
        }
        return ans;
    }
};
