class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        long hp=1,vp=1;
        long ans=0;
        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);
        int i=m-2,j=n-2;
        while(i>=0 && j>=0){
            if(horizontalCut[i]<=verticalCut[j]){
                // vertical cut
                ans+=verticalCut[j]*hp;
                vp++;
                j--;
            }else{
                // h cut
                ans+=horizontalCut[i]*vp;
                hp++;
                i--;
            }
        }
        while(i>=0){
            ans+=horizontalCut[i]*vp;
            i--;
            hp++;
        }
        while(j>=0){
            ans+=verticalCut[j]*hp;
            vp++;
            j--;

        }
        return ans;
    }
}
