class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.begin(), horizontalCut.end());
        sort(verticalCut.begin(), verticalCut.end());
        int horizontalCount = 0, verticalCount = 0;
        long long res = 0;
        int h_inedx = m-2, v_index = n-2;
        while(h_inedx >= 0 || v_index >= 0){
            if(h_inedx == -1){
                res += verticalCut[v_index] * (horizontalCount+1);
                v_index--;
                verticalCount++;
            }
            else if(v_index == -1){
                res += horizontalCut[h_inedx] * (verticalCount+1);
                h_inedx--;
                horizontalCount++;
            }
            else{
                if(horizontalCut[h_inedx] > verticalCut[v_index]){
                    res += horizontalCut[h_inedx] * (verticalCount+1);
                    h_inedx--;
                    horizontalCount++;
                }
                else{
                    res += verticalCut[v_index] * (horizontalCount+1);
                    v_index--;
                    verticalCount++;
                }
            }
        }
        return res;
    }
};
