class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<long long, char>> cuts;
        for (int i=0;i<horizontalCut.size();i++) {
            cuts.push_back({horizontalCut[i],'H'});
        }
        for (int j=0;j<verticalCut.size();j++) {
            cuts.push_back({verticalCut[j],'V'});
        }
        sort(cuts.rbegin(), cuts.rend());
        int h = 1;
        int v = 1;
        long long total = 0;
        for (auto& cut : cuts){
            if (cut.second=='H'){
                total += cut.first*v;
                h++;
            }
            else{
                total += cut.first*h;
                v++;
            }
        }

        return total;
    }
};
