class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hC, vector<int>& vC) {
        sort(hC.begin(),hC.end(),greater<int>());
        sort(vC.begin(),vC.end(),greater<int>());
        int i=0,j=0;
        int h=1,v=1;
        long long ans=0;
        while(i<hC.size() || j<vC.size()){
            int costh=i<hC.size()? hC[i]:INT_MIN;
            int costv=j<vC.size()? vC[j]:INT_MIN;
            if(costh>=costv){
                ans+=(long long)costh*h;
                v++;
                i++;
            }
            else{
                ans+=(long long)costv*v;
                h++;
                j++;
            }
        }
        return ans;
    }
};
