class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:


        #0 = Horizontal cut
        #1 = Vertical cut 
        cuts = []

        for cut in horizontalCut:
            cuts.append([cut, 0])

        for cut in verticalCut:
            cuts.append([cut, 1])  
        cuts.sort() 
        vertical_count, horizontal_count = 1, 1
        ans = 0 

        while cuts:
            cut, cut_type = cuts.pop()
            if cut_type == 1:
                ans = ans + horizontal_count * cut
                vertical_count += 1
            elif cut_type == 0:
                ans = ans + vertical_count * cut
                horizontal_count += 1 
        return ans
