class Solution:
    def minimumCost(self, m: int, n: int, h: List[int], v: List[int]) -> int:

        h.sort()
        v.sort()
        res = 0

        i, j = m - 2, n - 2
        hsum = sum(h)
        vsum = sum(v)

        while i >= 0 and j >= 0:
            if h[i] >= v[j]:
                # Cut the largest horizontal
                res += h[i] + vsum
                hsum -= h[i]
                i -= 1
            else:
                res += v[j] + hsum
                vsum -= v[j]
                j -= 1

        if i >= 0:
            res += hsum
        if j >= 0:
            res += vsum

        return res
