class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.rbegin(), hc.rend());
        sort(vc.rbegin(), vc.rend());
        int hn = 1, vn = 1;
        int h_idx = 0, v_idx = 0;
        long long ans = 0;
        while (h_idx < m - 1 || v_idx < n - 1) {
            int th = h_idx < m - 1 ? hc[h_idx] : -1;
            int tv = v_idx < n - 1 ? vc[v_idx] : -1;
            if (th > tv) {
                ans += hn * th;
                vn ++;
                h_idx++;
            } else{
                ans += vn * tv;
                hn ++;
                v_idx++;
            }
        }
        return ans;
    }
};
