/**
 * @param {number} m
 * @param {number} n
 * @param {number[]} horizontalCut
 * @param {number[]} verticalCut
 * @return {number}
 */
var minimumCost = function (m, n, horizontalCut, verticalCut) {
  var arr = [];
  horizontalCut.sort((a, b) => b - a);
  verticalCut.sort((a, b) => b - a);
  var i1 = 0;
  var i2 = 0;
  var res = 0;

  horizontalCut.push(0);
  verticalCut.push(0);

  while (i1 < m && i2 < n) {
    var c1 = horizontalCut[i1] * (i2 + 1);
    var c2 = verticalCut[i2] * (i1 + 1);

    if (horizontalCut[i1] > verticalCut[i2]) {
      res += c1;
      ++i1;
    } else {
      res += c2;
      ++i2;
    }
  }

  return res;
};
