class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        priority_queue<pair<long long,long long>> pq;
        for(auto i: horizontalCut) {
            pq.push({i*1ll,0});
        }
        for(auto i: verticalCut) {
            pq.push({i*1ll,1});
        }
        long long ans=0;
        long long h=1;
        long long v=1;
        while(!pq.empty()){
            pair<int,int> top = pq.top();
            pq.pop();
            if(top.second == 0){
                ans+=v*top.first;
                h++;
            } else {
                ans+=h*top.first;
                v++;
            }
        }
        return ans;
    }
};
