#define ll long long
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& X, vector<int>& Y) {
        sort(X.rbegin(), X.rend());
        sort(Y.rbegin(), Y.rend());

        ll h = 1;
        ll v = 1;

        ll i = 0;
        ll j = 0;
        ll result = 0;
        while(i < (m-1) && j < (n-1)){
            if(X[i] > Y[j]){
                result += X[i] * v;
                h++;
                i++;
            }
            else{
                result += Y[j] * h;
                v++;
                j++;
            }
        }

        // cout << "CHECK" << endl;

        ll left = 0;
        while(i < (m-1)){
            left += X[i];
            i++;
        }
        result += left * v;
        left = 0;
        while(j < (n-1)){
            left += Y[j];
            j++;
        }
        result += left * h;
        return result;
    }
};
