class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        int c=1;
        int r=1;
        priority_queue<pair<int,int>> q;
        long long sum=0;
        for(auto e:hc) q.push({e,1});
        for(auto e:vc) q.push({e,0});
        while(q.size())
        {
            auto f=q.top();
            q.pop();
            if(f.second==1)
            {
                sum+=1LL*f.first*c;
                r++;
            }
            else{
                sum+=1LL*f.first*r;
                c++;
            }

        }
        return sum;

    }
};
