class Solution {
public:
     long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.rbegin(), h.rend());
        sort(v.rbegin(), v.rend());
        int hw = 1, vw = 1;
        int i = 0, j = 0;
        long long ans = 0;
        while(i < m-1 && j < n-1){
            if(h[i] > v[j]){
                ans += 1LL * h[i] * vw;
                hw++;
                i++;
            }else{
                ans += 1LL * v[j] * hw;
                vw++;
                j++;
            }
        }
        long long tot = 0;
        while(i < m-1){
            tot += h[i++];
        }
        ans += tot * vw;
        tot = 0;
        while(j < n-1){
            tot += v[j++];
        }
        ans += tot * hw;
        return ans;
    }
};
