function sortDescending(arr) {
    return arr.sort((a, b) => b - a);
}
function processCuts(horizontalCut, verticalCut, hPieces, vPieces) {
    let h = 0, v = 0;
    let totalCost = 0;
    while (h < horizontalCut.length && v < verticalCut.length) {
        if (horizontalCut[h] >= verticalCut[v]) {
            totalCost += horizontalCut[h] * vPieces;
            hPieces++;
            h++;
        } else {
            totalCost += verticalCut[v] * hPieces;
            vPieces++;
            v++;
        }
    }
    return { totalCost, h, v, hPieces, vPieces };
}
